class TreeNode:
    def __init__(self, label):
        self.label = label
        self.left = None
        self.right = None

def build_tree(n, labels, children):
    nodes = [None] + [TreeNode(labels[i]) for i in range(n)]
    for i in range(1, n + 1):
        l, r = children[i - 1]
        if l != 0:
            nodes[i].left = nodes[l]
        if r != 0:
            nodes[i].right = nodes[r]
    return nodes[1]

def in_order_traversal(node):
    if not node:
        return ""
    return in_order_traversal(node.left) + node.label + in_order_traversal(node.right)

def duplicate_labels(node, k):
    if not node or k == 0:
        return in_order_traversal(node)

    original_label = node.label
    node.label *= 2
    left_result = duplicate_labels(node.left, k - 1)
    right_result = duplicate_labels(node.right, k - 1)
    node.label = original_label

    without_duplication = in_order_traversal(node)
    with_duplication = left_result + node.label + right_result

    if with_duplication < without_duplication:
        node.label *= 2
        return with_duplication
    return without_duplication

def lexicographically_smallest_string(n, k, labels, children):
    root = build_tree(n, labels, children)
    return duplicate_labels(root, k)

