def count_divisible_by_25(s):
    def is_valid(num_str):
        if num_str[0] == '0' and len(num_str) > 1:
            return False
        return int(num_str) % 25 == 0

    def generate_combinations(s):
        x_indices = [i for i, c in enumerate(s) if c == 'X']
        underscore_indices = [i for i, c in enumerate(s) if c == '_']
        all_indices = x_indices + underscore_indices

        if not all_indices:
            return [s]

        combinations = []
        for digits in product('0123456789', repeat=len(all_indices)):
            temp = list(s)
            for idx, digit in zip(all_indices, digits):
                temp[idx] = digit
            if len(set(temp[i] for i in x_indices)) == 1:
                combinations.append(''.join(temp))
        return combinations

    possible_numbers = generate_combinations(s)
    return sum(1 for num in possible_numbers if is_valid(num))

