        for neighbor in tree[node]:
            if neighbor != parent:
                size[node] += dfs(neighbor, node)
        return size[node]

    dfs(1, -1)

    # Sort nodes by the size of their subtrees in descending order
    nodes_by_size = sorted(range(1, n + 1), key=lambda x: size[x], reverse=True)

    # Red's turn: color the largest k subtrees
    red_nodes = set()
    for node in nodes_by_size:
        if len(red_nodes) + size[node] <= k:
            red_nodes.update(dfs_collect(node, -1))

    # Blue's turn: color the largest subtrees that do not intersect with red nodes
    blue_nodes = set()
    for node in nodes_by_size:
        if node not in red_nodes and not any(neighbor in red_nodes for neighbor in tree[node]):
            blue_nodes.update(dfs_collect(node, -1))

    # Calculate the score
    r = len(red_nodes)
    b = len(blue_nodes)
    w = n - r - b
    return w * (r - b)

