        for j in range(m):
            row.append(int(data[index]))
            index += 1
        grid.append(row)
    return n, m, grid

def get_neighbors(x, y, n, m, grid):
    directions = [(0, -1, 1), (1, 0, 2), (0, 1, 4), (-1, 0, 8)]
    neighbors = []
    for dx, dy, mask in directions:
        nx, ny = x + dx, y + dy
        if 0 <= nx < n and 0 <= ny < m and not (grid[x][y] & mask):
            neighbors.append((nx, ny))
    return neighbors

def bfs(x, y, n, m, grid, visited):
    queue = [(x, y)]
    visited[x][y] = True
    room_size = 0
    while queue:
        cx, cy = queue.pop(0)
        room_size += 1
        for nx, ny in get_neighbors(cx, cy, n, m, grid):
            if not visited[nx][ny]:
                visited[nx][ny] = True
                queue.append((nx, ny))
    return room_size

def find_room_sizes(n, m, grid):
    visited = [[False] * m for _ in range(n)]
    room_sizes = []
    for i in range(n):
        for j in range(m):
            if not visited[i][j]:
                room_size = bfs(i, j, n, m, grid, visited)
                room_sizes.append(room_size)
    return sorted(room_sizes, reverse=True)

